series = ''.split('0')

for i in 0..series.count-1
  puts series[i]
end

greatest = 1

for s in 0..series.count-1
  if series[s].length >= 13
    subseries = series[s][0,13]
    product = 1
    for i in 0..(subseries.length-1)
      product = product * subseries[i].to_i
    end
    for i in 13..(series[s].length - 1)
      product = product / series[s][i - 13].to_i
      product = product * series[s][i].to_i
      if product > greatest then greatest = product end
    end
  end
end

puts greatest